import React, { useState, useEffect } from 'react'
import CommonSection from '../shared/CommonSection'
import { Container, Row, Col } from 'reactstrap'
import '../styles/tour.css'
import tourData from '../assets/data/tours'
import TourCard from './../shared/TourCard'
import SearchBar from './../shared/SearchBar'
import Newsletter from './../shared/Newsletter'
import hotel from '../assets/data/hotel'
import Card from '../shared/Card'


const Tour = () => {

  const [pageCount, setPageCount] = useState(0)
  const [page, setPage] = useState(0)
  const [display, setDisplay] = useState(false)

  const handlePage = () => {
    if(display){
      setDisplay(false)
    }else{
      setDisplay(true)
    }
  }


  useEffect(() => {
    const pages = Math.ceil(5 / 4) //ambil dari backend
    setPageCount(pages)
    setDisplay(false)
  }, [page])
  return (
    <div>
      <CommonSection title={'All Tours'} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className='pt-0'>
        <Container>
          <Row>
            <span onClick={handlePage}>Hotel Travel</span>
            {
              display &&
              <>
                {tourData?.map(
                  tour => <Col lg='3' className='mb-4' key={tour.id}>
                    <TourCard tour={tour} />
                  </Col>
                )}
              </>
            }

            {
              !display &&
              <>
                {hotel?.map(
                  hotel => <Col lg='3' className='mb-4' key={hotel.id}>
                    <Card hotel={hotel} />
                  </Col>
                )}
              </>
            }

            <Col lg='12'>
              <div className='pagination d-flex align-items-center justify-content-center mt-4 gap-3'>
                {[...Array(pageCount).keys()].map(number => (
                  <span key={number} onClick={() => setPage(number)}
                    className={page === number ? 'active__page' : ''}>
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </div>
  )
}

export default Tour
